  private validateCodeFences(nodes: MeldNode[]): void {
    // Since we're using the meld-ast parser with validateNodes=true and preserveCodeFences=true,
    // we can trust that the code fences are already valid.
    // This is just an extra validation layer to ensure code fence integrity
    for (const node of nodes) {
      if (node.type === 'CodeFence') {
        const codeFence = node as CodeFenceNode;
        const content = codeFence.content;

        // Skip empty code fences (should be rare but possible)
        if (!content) {
          continue;
        }

        // Split the content by lines
        const lines = content.split('\n');
        if (lines.length < 2) {
          throw new MeldParseError(
            'Invalid code fence: must have at least an opening and closing line',
            node.location || { start: { line: 1, column: 1 }, end: { line: 1, column: 1 } }
          );
        }

        // Get the first line (opening fence) and count backticks
        const firstLine = lines[0];
        let openTickCount = 0;
        for (let i = 0; i < firstLine.length; i++) {
          if (firstLine[i] === '`') {
            openTickCount++;
          } else {
            break;
          }
        }

        // Get the last line (closing fence) and count backticks
        const lastLine = lines[lines.length - 1];
        let closeTickCount = 0;
        for (let i = 0; i < lastLine.length; i++) {
          if (lastLine[i] === '`') {
            closeTickCount++;
          } else {
            break;
          }
        }

        if (openTickCount === 0) {
          throw new MeldParseError(
            'Invalid code fence: missing opening backticks',
            node.location || { start: { line: 1, column: 1 }, end: { line: 1, column: 1 } }
          );
        }

        if (closeTickCount === 0) {
          throw new MeldParseError(
            'Invalid code fence: missing closing backticks',
            node.location || { start: { line: 1, column: 1 }, end: { line: 1, column: 1 } }
          );
        }

        if (openTickCount !== closeTickCount) {
          throw new MeldParseError(
            `Code fence must be closed with exactly ${openTickCount} backticks, got ${closeTickCount}`,
            node.location || { start: { line: 1, column: 1 }, end: { line: 1, column: 1 } }
          );
        }
      }
    }
  }

  /**
   * Resolve a variable reference node
   * @param node - The variable reference node to resolve
   * @param context - The resolution context
   * @returns The resolved node
   */
  async resolveVariableReference(node: IVariableReference, context: ResolutionContext): Promise {
    try {
      // Ensure factory is initialized
      this.ensureFactoryInitialized();

      // Try to use the resolution client
      if (this.resolutionClient) {
        try {
          // Convert the node to string format for the client
          const nodeStr = `{{${node.valueType}.${node.identifier}${node.fields ? '.' + node.fields.map(f => f.value).join('.') : ''}}}`;
          // Use resolveVariableReference method which is in the interface
          const resolvedStr = await this.resolutionClient.resolveVariableReference(nodeStr, context);

          // Return the original node with updated information
          // Use type assertion since we're adding a property that's not in the interface
          return {
            ...node,
            resolvedValue: resolvedStr
          } as IVariableReference & { resolvedValue: string };
        } catch (error) {
          logger.warn('Error using resolutionClient.resolve', { 
            error, 
            node 
          });
        }
      }

      // If we get here, we couldn't resolve the variable
      logger.warn('No resolution client available for variable transformation');
      return node;
    } catch (error) {
      logger.warn('Failed to transform variable node', { error, node });
      return node;
    }
  }
} 
```


```javascript
import type { MeldNode } from '@core/syntax/types/index.js';
import type { ParserServiceLike } from '@core/shared-service-types.js';

/**
 * Service responsible for parsing Meld content into an Abstract Syntax Tree (AST).
 * Provides methods to parse and analyze Meld documents and fragments.
 * 
 * @remarks
 * The ParserService wraps the meld-ast parser to provide a consistent interface
 * for parsing Meld content. It adds location information to nodes and handles
 * error reporting.
 * 
 * This service is typically used as the first step in the Meld document processing
 * pipeline, converting raw text content into a structured AST that can be further
 * processed by other services.
 * 
 * Dependencies:
 * - meld-ast: For the underlying parsing functionality
 */
interface IParserService extends ParserServiceLike {
  /**
   * Parse Meld content into an AST using meld-ast.
   * 
   * @param content - The Meld content to parse
   * @returns A promise that resolves to an array of MeldNodes representing the AST
   * @throws {MeldParseError} If the content cannot be parsed
   * 
   * @example
   * ```ts
   * const content = '@text greeting = "Hello, world!"';
   * const nodes = await parserService.parse(content);
   * // nodes contains a DirectiveNode representing the @text directive
   * ```
   */
  parse(content: string): Promise;

  /**
   * Parse Meld content and provide location information for each node.
   * This is useful for error reporting and source mapping.
   * 
   * @param content - The Meld content to parse
   * @param filePath - Optional file path for better error messages
   * @returns A promise that resolves to an array of MeldNodes with location information
   * @throws {MeldParseError} If the content cannot be parsed
   * 
   * @example
   * ```ts
   * const content = '@text greeting = "Hello, world!"';
   * const nodes = await parserService.parseWithLocations(content, 'example.meld');
   * // nodes contains a DirectiveNode with location information
   * ```
   */
  parseWithLocations(content: string, filePath?: string): Promise;
} 

export type { IParserService }; 
